echo '<html><head></head><body>' > index.html; for n in $(lv2ls); do filename=$(echo info/$(echo $(echo $n | sha256sum) | cut -d ' ' -f 1).html); echo "<p><a href=\"$filename\">$(cat $filename | head -n3 | tail -n 1 | tr -s ' ' | cut -d ' ' -f 2-) $(head -n1 $filename)</a></p>" >> index.html;  done; echo '</body></html>' >> index.html
